		}
		return numWords.substring(num, num + 1);
	}

	/**
	 * 
	 * @return
	 */
	public static int toNum(String str) {
		int flag = 1;// 
		int var = 0;
		char[] arr = str.toCharArray();
		if (arr[0] == '') {
			str = str.replace("", "");

			flag = -1;
		}
		if (str.length() == 1 &&str.equals("")) {
			return flag*100;
		}
		// 
		if (str.length() > 1 && !str.contains("") && !str.contains("")) {
			char[] chArr = str.toCharArray();
			int temp = 1;
			for (int i = chArr.length - 1; i >= 0; i--) {
				String s = "" + chArr[i];
				var += temp * toSingleNum(s);
				temp *= 10;
			}
			return var * flag;
		}

		if (str.contains("")) {
			var = 100 * toSingleNum(str.substring(0, str.indexOf('')));// 
			str = str.substring(str.indexOf('') + 1);
			if (str.contains("")) {
				var += toSingleNum(str.substring(str.indexOf("") + 1));// 
				return var * flag;
			}
		}

		if (str.length() == 1 && var >= 100) { // var  100
			var += 10 * toSingleNum(str); // 
			return var * flag;
		}

		if (str.contains("")) {
			if (str.indexOf('') == 0) {
				var += 10; // 
			}
			var += 10 * toSingleNum(str.substring(0, str.indexOf('')));// 
			str = str.substring(str.indexOf('') + 1);
		}

		if (str.length() != 0) {
			var += toSingleNum(str);
		}

		return var * flag;
	}

	@Test
	public void test2() {
		// Java int Max 2147483647  Min-2147483648
		// System.out.println(toNum(""));
		System.out.println(toNum(""));
	}

	/**
	 * 
	 * @param num
	 * @return
	 */
	public static String toChStr(int num) {

		if (num == -2147483648) {
			throw new DemoException("(-21474836472147483647)");
		}
		String varStr = "";

		if (num < 0) {
			varStr += "";
			num = num * -1; // 
		}

		// 
		if (num > 999) {
			// 1234
			char[] CharArr = Integer.toString(num).toCharArray();
			for (char c : CharArr) {
				String s = "" + c;
				varStr += toSingleChStr(Integer.parseInt(s));
			}
			return varStr;
		}
		if (num == 0) { // 
			return "";
		}

		if (num / 10 == 1) { //  
			varStr += "";
			if (num % 10 != 0) {
				varStr += toSingleChStr(num % 10);
			}
			return varStr;
		}

		if (num / 100 > 0) {
			varStr += toSingleChStr(num / 100) + "";
			if (num % 100 == 0) {// 
				return varStr;
			} else if (num % 100 < 10) { // 
				return varStr += "" + toSingleChStr(num % 100);
			}
		}

		num %= 100;
		if (num / 10 != 0) {
			varStr += toSingleChStr(num / 10) + "";// 
		}

		if (num % 10 != 0) {
			varStr += toSingleChStr(num % 10); // 
		}

		return varStr;
	}

	/**
	 * 
	 * @param str
	 */
	public static void manipulateNum(String str) {
		String[] strArray = str.trim().split("\\s+");
		if (strArray.length != 3) {
			throw new DemoException("");
		}

		String operator = strArray[1];
		String varStr = strArray[0];
		String numStr = strArray[2];

		int var = getVar(varStr);
		int num = toNum(numStr);

		switch (operator) {
		case "":
			var -= num;
			break;
		case "":
			var += num;
			break;
		case "":
			var *= num;
			break;
		case "":
			if (num == 0) {
				throw new DemoException("");
			} else {
				var /= num;
			}
			break;
		case "":
			var %= num;
			break;
		default:
			throw new IllegalArgumentException("" + operator + "  ");
		}

		setVar(varStr, var);
	}

	/**
	 * 
	 * @param str
	 * @return
	 */
	public static boolean isManipulate(String str) {
		String[] array = str.trim().split("\\s+");

		String[] keywords = { "", "", "", "", "" };

		// array[1]
		if (array.length == 1) {
			throw new DemoException("");
		}
		String symbol = array[1];

		for (String s : keywords) {
			if (symbol.equals(s)) {
				return true;
			}
		}
		return false;
	}

	@Test
	public void testSymbol() {
		System.out.println(isManipulate("   "));
	}

	/**
	 * Map
	 * @param str 
	 * @return
	 */
	private static int getVar(String varStr) {
		if (isVar(varStr)) {
			return map.get(varStr);
		} else {
			throw new DemoException("" + varStr + " ");
		}
	}

	/**
	 * map
	 * @param varStr 
	 * @param var 
	 */
	private static void setVar(String varStr, int var) {
		map.put(varStr, var);
	}

	/**
	 * 
	 * @param varStr 
	 * @return
	 */
	private static boolean isVar(String varStr) {
		return map.get(varStr) != null ? true : false;
	}
}



